import React, { useState } from "react";

export function Login() {
  // Setting our component's initial state
  const [email, setEmail] = useState()
  const [password, setPassword] = useState()


  // Handles updating email component state when the user types into the input field
  function handleEmailInputChange(event) {
    const { name, value } = event.target;
    setEmail({ ...email, [name]: value })
  };

  // Handles updating email component state when the user types into the input field
  function handlePasswordInputChange(event) {
    const { name, value } = event.target;
    setPassword({ ...password, [name]: value })
  };

  // Handles credential verification when submit button is clicked
  function handleFormSubmit(event) {
    event.preventDefault();
    if (email === "" || password === "") {
      alert("Please enter an email and password")
    } else {
      alert("Login successful!")
      console.log(email + "and " + password)
      // Need to verify login credentials with database
      // Need to redirect user to homepage
    }
  };

  // Function to verify login credentials goes here



  return (
    <div class="row">
      <div class="col-md-6">
        <h2>Login</h2>

        <form class="form login-form">
          <div class="form-group">
            <label for="email-login">email:</label>
            <input class="form-input" type="text" id="email-login" name="email" onChange={handleEmailInputChange} />
          </div>
          <div class="form-group">
            <label for="password-login">password:</label>
            <input class="form-input" type="password" id="password-login" name="password" onChange={handlePasswordInputChange} />
          </div>
          <div class="form-group">
            <button class="btn btn-primary" type="submit" onClick={handleFormSubmit}>login</button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default Login;
