import React, { useState } from "react";

export function Login() {
  // Setting our component's initial state
  const [email, setEmail] = useState("")
  const [password, setPassword] = useState("")


  // Handles updating email component state when the user types into the input field
  function handleEmailInputChange(event) {
    const { value } = event.target;
    setEmail({ value })
  };

  // Handles updating email component state when the user types into the input field
  function handlePasswordInputChange(event) {
    const { value } = event.target;
    setPassword({ value })
  };

  // Handles credential verification when submit button is clicked
  function handleFormSubmit(event) {
    event.preventDefault();
    if (email === "" || password === "") {
      alert("Please enter an email and password")
    } else {
      alert("Login successful!")
      console.log(email + "and " + password)
      // Need to verify login credentials with database
      // Need to redirect user to homepage
    }
  };

  // Function to verify login credentials goes here



  return (
    <div className="row">
      <div className="col-md-6">
        <h2>Login</h2>

        <form className="form login-form">
          <div className="form-group">
            <label htmlFor="email-login">email:</label>
            <input className="form-input" type="text" id="email-login" name="email" onChange={handleEmailInputChange} />
          </div>
          <div className="form-group">
            <label htmlFor="password-login">password:</label>
            <input className="form-input" type="password" id="password-login" name="password" onChange={handlePasswordInputChange} />
          </div>
          <div className="form-group">
            <button className="btn btn-primary" type="submit" onClick={handleFormSubmit}>login</button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default Login;
